import React, { useState } from 'react';
import './Section.css'; // Shared styles for sections

function HomeScreen() {
  const [imageSrc, setImageSrc] = useState('/camera-icon.png'); // Start with the camera icon

  // Simulate image capture
  const handleCameraClick = () => {
    // Simulating a delay to show that the image is being "analyzed"
    setTimeout(() => {
      // Replace 'analyzed-image.png' with the real image URL after capturing
      setImageSrc('analyzed-image.png');
    }, 1000); // Simulate 1 second delay
  };

  return (
    <div className="section home-screen">
      <div className="logo">
        <img src="/logo.png" alt="Scanwise AI Logo" />
      </div>
      <div className="content">
        <div className="camera-icon-placeholder" onClick={handleCameraClick}>
          <img src={imageSrc} alt="Camera or Analyzed Image" className="placeholder-icon" />
        </div>
      </div>
      <div className="actions">
        <button className="scan-btn">SCAN QR CODE</button>
      </div>
    </div>
  );
}

export default HomeScreen;
