import React, { useState, useRef } from 'react';
import './Section.css';

function HomeScreen({ onNavigate }) {
  const [imageSrc, setImageSrc] = useState(null); // State to hold captured image
  const videoRef = useRef(null); // Ref for the video element

  // Start the user's camera
  const handleCameraClick = () => {
    // Access the user's camera
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        // Attach the video stream to the video element
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      })
      .catch((error) => {
        console.error('Error accessing the camera:', error);
      });
  };

  // Capture an image from the video stream
  const handleCapture = () => {
    const video = videoRef.current;
    if (video) {
      // Create a canvas to draw the captured frame
      const canvas = document.createElement('canvas');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      const capturedImage = canvas.toDataURL('image/png'); // Convert to image data
      setImageSrc(capturedImage); // Update state with captured image
      video.srcObject.getTracks().forEach(track => track.stop()); // Stop the video stream
    }
  };

  return (
    <div className="section home-screen">
      <div className="logo">
        <img src="/logo.png" alt="Scanwise AI Logo" />
      </div>
      <div className="content">
        {/* Conditionally render video for live feed or captured image */}
        {!imageSrc ? (
          <div className="camera-icon-placeholder" onClick={handleCameraClick}>
            <p>Click to Access Camera</p>
          </div>
        ) : (
          <div className="image-placeholder">
            <img src={imageSrc} alt="Captured" />
          </div>
        )}
        {/* Video element to display camera feed */}
        <video ref={videoRef} autoPlay style={{ display: imageSrc ? 'none' : 'block', width: '100%' }}></video>
        {/* Capture Button */}
        {!imageSrc && (
          <button onClick={handleCapture}>Capture Image</button>
        )}
      </div>
      <div className="actions">
        <button className="scan-btn" onClick={() => onNavigate('safe')}>Go to Safe Screen</button>
      </div>
    </div>
  );
}

export default HomeScreen;
